CREATE TABLE IF NOT EXISTS users ( id BIGINT GENERATED BY DEFAULT AS IDENTITY NOT NULL, name VARCHAR(250), email VARCHAR (254), CONSTRAINT pk_users PRIMARY KEY(id), CONSTRAINT uq_users_name UNIQUE(name) );
CREATE TABLE IF NOT EXISTS categories ( id BIGINT GENERATED BY DEFAULT AS IDENTITY NOT NULL, name VARCHAR(50), CONSTRAINT pk_categories PRIMARY KEY(id), CONSTRAINT uq_category_name UNIQUE(name) );